import ExpenseItem from "./ExpenseItem";
import "./Expenses.css";
import Card from "../UI/Card";
import ExpensesFilter from "./ExpensesFilter";
import { useState } from "react";
const Expenses = (props) => {
  const [filteredYear, setFilteredYear] = useState("2022");
  const saveFilterYear = (enteredFilterYear) => {
    setFilteredYear(enteredFilterYear);
    let filteredDateArray = props.items.filter(
      (filterDate) => (filterDate = enteredFilterYear)
    );
    return (
      <ExpenseItem
        key={filteredDateArray.id}
        title={filteredDateArray.title}
        amount={filteredDateArray.amount}
        date={filteredDateArray.date}
      ></ExpenseItem>
    );
  };
  return (
    <div>
      <Card className="expenses">
        <ExpensesFilter
          selected={filteredYear}
          onSaveFlilterYear={saveFilterYear}
        ></ExpensesFilter>
        {props.items.map((expense) => (
          <ExpenseItem
            key={expense.id}
            title={expense.title}
            amount={expense.amount}
            date={expense.date}
          ></ExpenseItem>
        ))}
      </Card>
    </div>
  );
};

export default Expenses;
